BlackBox.templateD:
~SIGD[~SYM[0]][2];

always_ff @(posedge ~CLK[0] or negedge ~RST[0])
if (~ ~RST[0]) begin
  ~SYM[0] <= ~ARG[1];
end else begin
  ~SYM[0] <= ~ARG[2];
end

assign ~RESULT = ~SYM[0];

~SIGD[~SYM[0]][3];

always_ff @(posedge ~CLK[0] or negedge ~RST[0])
if (~ ~RST[0]) begin
  ~SYM[1] <= ~ARG[1];
end else begin
  if (~ARG[2]) begin
    ~SYM[1] <= ~ARG[3];
  end
end

assign ~RESULT = ~SYM[1];
