BlackBox.templateD:
reg ~SIGD[~SYM[1]][2];

always @ (posedge ~CLK[0] or negedge ~RST[0])
if (~ ~RST[0]) begin
  ~SYM[1] <= ~ARG[1];
end else begin
  ~SYM[1] <= ~ARG[2];
end

assign ~RESULT = ~SYM[1];

regEn_~SYM[0] : block
  signal ~SYM[1] : ~TYP[1];
  signal ~SYM[2] : ~TYP[2];
  signal ~SYM[3] : ~TYP[3];
begin
  ~SYM[1] <= ~ARG[1];
  ~SYM[2] <= ~ARG[2];

  process(~CLK[0],~RST[0],~SYM[1],~SYM[2])
  begin
    if ~RST[0] = '0' then
      ~SYM[3] <= ~SYM[1];
    elsif rising_edge(~CLK[0]) then
      if ~SYM[2] then
        ~SYM[3] <= ~ARG[3];
      end if;
    end if;
  end process;

  ~RESULT <= ~SYM[3];
end block;
