BlackBox.templateD:
blockram_~SYM[0] : block
  signal ~SYM[1] : ~TYP[4] := ~LIT[4]; -- ram
  signal ~SYM[2] : ~TYP[8]; -- inp
  signal ~SYM[3] : ~TYP[8]; -- outp
begin
  ~SYM[2] <= ~ARG[8];

  process(~CLK[3])
  begin
    if rising_edge(~CLK[3]) then
      if ~ARG[7] then
        ~SYM[1](to_integer(~ARG[5])) <= ~SYM[2];
      end if;
      ~SYM[3] <= ~SYM[1](to_integer(~ARG[6]));
    end if;
  end process;

  ~RESULT <= ~SYM[3];
end block;
