BlackBox.template:
~SYM[0]_block : block
  type ram_array is array (natural range <>) of ~TYP[5];
  signal ~SYM[1] : ram_array((~ARG[0]-1) downto 0); -- ram
  signal ~SYM[2] : ~TYP[5]; -- inp
  signal ~SYM[3] : ~TYP[5]; -- outp
begin
  ~SYM[2] <= ~ARG[5];

  process(~CLK[5]) is
  begin
    if rising_edge(~CLK[5]) then
      if ~ARG[4] then
        ~SYM[1](to_integer(~ARG[2])) <= ~SYM[2];
      end if;
      ~SYM[3] <= ~SYM[1](to_integer(~ARG[3]));
    end if;
  end process;

  ~RESULT <= ~SYM[3];
end block;
